    /// <summary>
    /// Tests the decryption of C1-packets with the <see cref="PipelinedDecryptor"/>.
    /// </summary>
    /// <returns>The task.</returns>
    @Test
    public void C1Decrypt() {
        byte[] expected = DatatypeConverter.parseBase64Binary("");
        byte[] packet = DatatypeConverter.parseBase64Binary("");
        ByteBuf expected_buff = Unpooled.wrappedBuffer(expected);
        ByteBuf packet_buff = Unpooled.wrappedBuffer(packet);
        ByteBuf actual_buff = S6EP3.deCrypt(packet_buff, (short) 0);
        comparePacket(expected_buff, actual_buff);
    }

