    /// <summary>
    /// Tests the C1-Packet encryption. C1 packet to the client are not getting encrypted, so we check this.
    /// </summary>
    /// <returns>The task.</returns>
    @Test
    public void C1NonEncrypt() {
        byte[] packet = new byte[]{(byte) 0xC1, 0x05, (byte) 0xAB, (byte) 0xBC, (byte) 0xDE};
        byte[] expected = new byte[]{(byte) 0xC1, 0x05, (byte) 0xAB, (byte) 0xBC, (byte) 0xDE};
        ByteBuf expected_buff = Unpooled.wrappedBuffer(expected);
        ByteBuf packet_buff = Unpooled.wrappedBuffer(packet);
        ByteBuf actual_buff = S6EP3.enCrypt(packet_buff, (short) 0);
        comparePacket(expected_buff, actual_buff);
    }

